var http = require("http"), server = http.createServer();

server.on("request", function(a, b) {
    b.writeHead(200, {
        "content-type": "text/plain"
    }), b.write("hello...world"), b.end();
});

var port = 8e3;

server.listen(port), server.once("listening", function() {
    console.log("please .....wait till port %d get ready", port);
});

var _filename = "test.txt", fs = require("fs");

fs.readFile(_filename, "utf8", function(a, b) {
    a ? console.error(a) : console.log("got file content:", b);
});